import React, { useEffect, useState } from "react";
import "./App.css";
import { ConnectButton, Modal } from "web3uikit";
import Coin from "./components/Coin";
import {abouts} from "./about";
import { useMoralisWeb3Api, useMoralis } from "react-moralis";

const App = () => {

  const [btc, setBtc] = useState();
  const [eth, setEth] = useState();
  const [matic, setMatic] = useState();
  const [modalPrice, setModalPrice] = useState();
  const Web3Api = useMoralisWeb3Api();
  const {Moralis, isInitialized} = useMoralis();
  const [visible, setVisible] = useState(false);
  const [modalToken, setModalToken] = useState();


  async function getRatio(tick, setPerc) {

    const Votes = Moralis.Object.extend("Votes");
    const query = new Moralis.Query(Votes);
    query.equalTo("ticker", tick);
    query.descending("createdAt");
    const results = await query.first();
    let up = Number(results.attributes.up);
    let down = Number(results.attributes.down);
    let ratio = Math.round(up/(up+down)*100);
    setPerc(ratio);
  }

  useEffect(() => {
    if(isInitialized){
    getRatio("BTC", setBtc);
    getRatio("ETH", setEth);
    getRatio("MATIC", setMatic);
    }

    async function createLiveQuery(){
      let query = new Moralis.Query('Votes');
      let subscription = await query.sunscribe();
      subscription.on('update', (object) => {

        if(object.attributes.ticker === "MATIC") {
          getRatio("MATIC", setLink);
        }else if(object.attributes.ticker === "ETH") {
            getRatio("ETH", setEth);
        }if(object.attributes.ticker === "BTC") {
              getRatio("BTC", setBtc);
        }

      });
    }

  }, [isInitialized]);

 useEffect(() => {

   async function fetchTokenPrice() {
     const options = {
       address:
       abouts[abouts.findIndex((x) => x.token === modalToken)].address,
     };
     const price = await Web3Api.token.getTokenPrice(options);
     setModalPrice(price.usdPrice.toFixed(2));
   }

   if(modalToken){
     fetchTokenPrice()
   }
 }, [modalToken]);

  return (
    <>
      <div className="header">
        <div className="logo">
        Sentiment Tracker
        </div>
        <ConnectButton/>
      </div>
      <div className="instructions">
      Do you think these tokens are going Up or Down?
      </div>
      <div className="list" >
        <Coin
          perc={btc}
          setPerc={setBtc}
          token={"BTC"}
          setModalToken={setModalToken}
          setVisible={setVisible}
        />
         <Coin
          perc={eth}
          setPerc={setBtc}
          token={"ETH"}
          setModalToken={setModalToken}
          setVisible={setVisible}
        />
         <Coin
          perc={matic}
          setPerc={setBtc}
          token={"MATIC"}
          setModalToken={setModalToken}
          setVisible={setVisible}
        />
      </div>
      <Modal
       isVisible={visible}
       onCloseButtonPressed={() => setVisible(false)}
       hasFooter={false}
       title={modalToken}
      >
        <div>
        <span style={{ color: "white" }}>{`Price:`}</span>
          {modalPrice}$
        </div>
        <div>
          <span style={{ color: "white"}}>{`About`}</span>
        </div>
        <div>
          {modalToken &&
            abouts[abouts.findIndex((x) => x.token === modalToken)].about}
        </div>
      </Modal>
    </>
  );
};

export default App;
